import 'package:army_rent_clothes/model/brand_model.dart';
import 'package:flutter/material.dart';

class ProductDetailModel {
  final int itemIndex;
  final Image thumbNail;
  final List<Image> itemImages;
  final BrandModel brand;
  final String title;
  final int originalPrice;
  final int sellingPrice;
  final int discount;
  final String detailHtml;

  ProductDetailModel({
    required this.itemIndex,
    required this.thumbNail,
    required this.itemImages,
    required this.brand,
    required this.title,
    required this.originalPrice,
    required this.sellingPrice,
    required this.detailHtml,
    discount,
  })  : assert(originalPrice >= sellingPrice),
        discount = discount ?? sellingPrice == originalPrice
            ? 0
            : ((1 - sellingPrice / originalPrice) * 100).round();
}

